			body, html {
				width: 100%;
				height: 100%;
			}

			body {
				background-color: #161D47;
			}

			body.done {
				background-color: #FFFFFF;
			}

			.preloader {
				width: 100%;
				height: 100%;
				background-color: #161D47;
				background-color: #0A0F24;
				position: fixed;
				top: 0;
				z-index: 9999;
				display: flex;
				justify-content: center;
    			-ms-align-items: center;
    			align-items: center;
			}

			.preloader.slideOutDown {
				background-color: #CED242;
				-webkit-animation-delay: 1.5s;
				-o-animation-delay: 1.5s;
				animation-delay: 1.5s;
				-webkit-animation-duration: 1.2s;
				-o-animation-duration: 1.2s;
				animation-duration: 1.2s;
				-webkit-transition: background 1.5s 1.5s ease-in;
				-o-transition: background 1.5s 1.5s ease-in;
				transition: background 1.5s 1.5s ease-in;
			}

			.logo {
				height: 50px;
				width: auto;
				display: block;
				float: left;
			}

			.sprite1,
			.sprite2 {
			    display: block;
			    float: left;
			}

			.sprite1 {
				margin-right: 7px;
				-webkit-animation-delay: .3s;
				-o-animation-delay: .3s;
				animation-delay: .3s;
			}

			.sprite1.fadeOut,
			.sprite2.fadeOut {
				-webkit-animation-delay: .5s;
				-o-animation-delay: .5s;
				animation-delay: .5s;
